        i0.ÉµÉµelementEnd();
        i0.ÉµÉµelementStart(203, "span", 7);
        i0.ÉµÉµtext(204, "\u0628\u0646\u0627\u0621 \u0645\u0633\u062A\u0642\u0628\u0644 \u0627\u0644\u0645\u0645\u0644\u0643\u0629 \u0627\u0644\u0639\u0631\u0628\u064A\u0629 \u0627\u0644\u0633\u0639\u0648\u062F\u064A\u0629");
        i0.ÉµÉµelementEnd()();
        i0.ÉµÉµelementStart(205, "p", 6);
        i0.ÉµÉµtext(206, " Abraj Al Khaleej is proud to contribute to Saudi Arabia's Vision 2030, a strategic framework to reduce Saudi Arabia's dependence on oil, diversify its economy, and develop public service sectors such as health, education, infrastructure, recreation, and tourism. ");
        i0.ÉµÉµelementEnd();
        i0.ÉµÉµelementStart(207, "p", 7);
        i0.ÉµÉµtext(208, " \u062A\u0641\u062E\u0631 \u0634\u0631\u0643\u0629 \u0623\u0628\u0631\u0627\u062C \u0627\u0644\u062E\u0644\u064A\u062C \u0628\u0627\u0644\u0645\u0633\u0627\u0647\u0645\u0629 \u0641\u064A \u0631\u0624\u064A\u0629 \u0627\u0644\u0633\u0639\u0648\u062F\u064A\u0629 2030\u060C \u0627\u0644\u0625\u0637\u0627\u0631 \u0627\u0644\u0627\u0633\u062A\u0631\u0627\u062A\u064A\u062C\u064A \u0644\u062A\u0642\u0644\u064A\u0644 \u0627\u0639\u062A\u0645\u0627\u062F \u0627\u0644\u0645\u0645\u0644\u0643\u0629 \u0639\u0644\u0649 \u0627\u0644\u0646\u0641\u0637\u060C \u0648\u062A\u0646\u0648\u064A\u0639 \u0627\u0642\u062A\u0635\u0627\u062F\u0647\u0627\u060C \u0648\u062A\u0637\u0648\u064A\u0631 \u0642\u0637\u0627\u0639\u0627\u062A \u0627\u0644\u062E\u062F\u0645\u0627\u062A \u0627\u0644\u0639\u0627\u0645\u0629 \u0645\u062B\u0644 \u0627\u0644\u0635\u062D\u0629 \u0648\u0627\u0644\u062A\u0639\u0644\u064A\u0645 \u0648\u0627\u0644\u0628\u0646\u064A\u0629 \u0627\u0644\u062A\u062D\u062A\u064A\u0629 \u0648\u0627\u0644\u062A\u0631\u0641\u064A\u0647 \u0648\u0627\u0644\u0633\u064A\u0627\u062D\u0629. ");
        i0.ÉµÉµelementEnd();
        i0.ÉµÉµelementStart(209, "p", 6);
        i0.ÉµÉµtext(210, " Our construction projects align with Vision 2030's goals of economic diversification, infrastructure development, and enhancing the quality of life for all citizens. We are committed to sustainable building practices and innovative solutions that support the Kingdom's ambitious transformation. ");
        i0.ÉµÉµelementEnd();
        i0.ÉµÉµelementStart(211, "p", 7);
        i0.ÉµÉµtext(212, " \u062A\u062A\u0645\u0627\u0634\u0649 \u0645\u0634\u0627\u0631\u064A\u0639\u0646\u0627 \u0627\u0644\u0625\u0646\u0634\u0627\u0626\u064A\u0629 \u0645\u0639 \u0623\u0647\u062F\u0627\u0641 \u0631\u0624\u064A\u0629 2030 \u0627\u0644\u0645\u062A\u0645\u062B\u0644\u0629 \u0641\u064A \u062A\u0646\u0648\u064A\u0639 \u0627\u0644\u0627\u0642\u062A\u0635\u0627\u062F \u0648\u062A\u0637\u0648\u064A\u0631 \u0627\u0644\u0628\u0646\u064A\u0629 \u0627\u0644\u062A\u062D\u062A\u064A\u0629 \u0648\u062A\u062D\u0633\u064A\u0646 \u062C\u0648\u062F\u0629 \u0627\u0644\u062D\u064A\u0627\u0629 \u0644\u062C\u0645\u064A\u0639 \u0627\u0644\u0645\u0648\u0627\u0637\u0646\u064A\u0646. \u0646\u062D\u0646 \u0645\u0644\u062A\u0632\u0645\u0648\u0646 \u0628\u0645\u0645\u0627\u0631\u0633\u0627\u062A \u0627\u0644\u0628\u0646\u0627\u0621 \u0627\u0644\u0645\u0633\u062A\u062F\u0627\u0645\u0629 \u0648\u0627\u0644\u062D\u0644\u0648\u0644 \u0627\u0644\u0645\u0628\u062A\u0643\u0631\u0629 \u0627\u0644\u062A\u064A \u062A\u062F\u0639\u0645 \u0627\u0644\u062A\u062D\u0648\u0644 \u0627\u0644\u0637\u0645\u0648\u062D \u0644\u0644\u0645\u0645\u0644\u0643\u0629. ");
