  get vision2030Goals() {
    return this.t.vision2030.goals;
  }

  isMenuOpen = false;

  // Toggle language
  toggleLanguage(): void {
    this.currentLanguage = this.currentLanguage === 'ar' ? 'en' : 'ar';
    // Update document direction
    document.documentElement.dir = this.currentLanguage === 'ar' ? 'rtl' : 'ltr';
    document.documentElement.lang = this.currentLanguage;
    // Update projects
    this.updateProjects();
  }

  ngOnInit(): void {
    // Set initial language
    document.documentElement.dir = this.currentLanguage === 'ar' ? 'rtl' : 'ltr';
    document.documentElement.lang = this.currentLanguage;
    
    // Smooth scroll behavior
    document.documentElement.style.scrollBehavior = 'smooth';
    
    // Mobile menu toggle
    const hamburger = document.querySelector('.hamburger');
    const navMenu = document.querySelector('.nav-menu');
    
    if (hamburger && navMenu) {
      hamburger.addEventListener('click', () => {
        this.toggleMenu();
      });
    }
    
    // Update projects with translations
    this.updateProjects();
  }

  updateProjects(): void {
    const projectData = this.t.projects.items;
    this.projects.forEach((project, index) => {
      if (projectData[index]) {
        project.name = projectData[index].name;
        project.category = projectData[index].category;
      }
    });
  }

  toggleMenu(): void {
    this.isMenuOpen = !this.isMenuOpen;
    const navMenu = document.querySelector('.nav-menu');
    const hamburger = document.querySelector('.hamburger');
    
    if (navMenu) {
      navMenu.classList.toggle('active');
    }
    if (hamburger) {
      hamburger.classList.toggle('active');
    }
  }

  scrollToSection(sectionId: string): void {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      // Close mobile menu if open
      if (this.isMenuOpen) {
        this.toggleMenu();
      }
    }
  }

  onImageError(event: Event) {
  const target = event.target as HTMLElement;

  if (target) {
    target.style.display = 'none';

    const nextEl = target.nextElementSibling as HTMLElement;
    if (nextEl) {
      nextEl.style.display = 'flex';
    }
  }
}

