<section id="home" class="hero" [@fadeIn]>
  <div class="hero-overlay"></div>
  <div class="hero-content">
    <h1 class="hero-title" [@slideInLeft]>
      {{ t.hero.title }}
      <span class="arabic-subtitle">{{ t.hero.subtitle }}</span>
    </h1>
    <p class="hero-description" [@slideInRight]>
      {{ t.hero.description }}
    </p>
    <div class="hero-buttons" [@fadeIn]>
      <button class="btn btn-primary" (click)="scrollToSection('projects')">{{ t.hero.btnProjects }}</button>
      <button class="btn btn-secondary" (click)="scrollToSection('contact')">{{ t.hero.btnContact }}</button>
    </div>
  </div>
  <div class="scroll-indicator">
    <span></span>
  </div>
</section>

<!-- About Section -->
<section id="about" class="about">
  <div class="container">
    <div class="section-header" [@fadeIn]>
      <h2>{{ t.about.title }}</h2>
      <p class="section-subtitle">{{ t.about.subtitle }}</p>
    </div>
    <div class="about-content">
      <div class="about-text" [@slideInLeft]>
        <h3>{{ t.about.story }}</h3>
        <p>
          {{ t.about.storyText1 }}
        </p>
        <p>
          {{ t.about.storyText2 }}
        </p>
        <div class="stats">
          <div class="stat-item">
            <h4>500+</h4>
            <p>{{ t.about.stats.projects }}</p>
          </div>
          <div class="stat-item">
            <h4>15+</h4>
            <p>{{ t.about.stats.years }}</p>
          </div>
          <div class="stat-item">
            <h4>200+</h4>
            <p>{{ t.about.stats.team }}</p>
          </div>
        </div>
      </div>
      <div class="about-image" [@slideInRight]>
        <div class="image-placeholder">
          <div class="construction-icon">ğŸ—ï¸</div>
        </div>
      </div>
    </div>
  </div>
</section>

<!-- Services Section -->
<section id="services" class="services">
  <div class="container">
    <div class="section-header" [@fadeIn]>
      <h2>{{ t.services.title }}</h2>
      <p class="section-subtitle">{{ t.services.subtitle }}</p>
    </div>
    <div class="services-grid" [@stagger]>
      <div class="service-card" *ngFor="let service of services" [@fadeIn]>
        <div class="service-icon">{{ service.icon }}</div>
        <h3>{{ service.title }}</h3>
        <p>{{ service.description }}</p>
      </div>
    </div>
  </div>
</section>

<!-- Projects Section -->
<section id="projects" class="projects">
  <div class="container">
    <div class="section-header" [@fadeIn]>
      <h2>{{ t.projects.title }}</h2>
      <p class="section-subtitle">{{ t.projects.subtitle }}</p>
    </div>
    <div class="projects-grid" [@stagger]>
      <div class="project-card" *ngFor="let project of projects" [@fadeIn]>
        <div class="project-image">
          <img [src]="project.image" [alt]="project.name">
          <div class="project-overlay">
            <h3>{{ project.name }}</h3>
            <p>{{ project.category }}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>

