<!-- Contact Section -->
<section id="contact" class="contact">
  <div class="container">
    <div class="section-header" [@fadeIn]>
      <h2>{{ t.contact.title }}</h2>
      <p class="section-subtitle">{{ t.contact.subtitle }}</p>
    </div>
    <div class="contact-content">
      <div class="contact-info" [@slideInLeft]>
        <div class="info-item">
