            <p>+966 XX XXX XXXX</p>
          </div>
        </div>
        <div class="info-item">
          <div class="info-icon">âœ‰ï¸</div>
          <div>
            <h4>{{ t.contact.email }}</h4>
            <p>info&#64;abraj-kh.com</p>
          </div>
        </div>
