  ngOnInit(): void {
    document.documentElement.style.scrollBehavior = 'smooth';
    this.attachMobileMenuHandler();
    this.updateLanguageDirection();
  }

  setLanguage(lang: Lang): void {
    if (this.currentLang !== lang) {
      this.currentLang = lang;
      this.updateLanguageDirection();
    }
  }

  toggleLanguage(): void {
    this.setLanguage(this.currentLang === 'en' ? 'ar' : 'en');
  }

  t(en: string, ar: string): string {
    return this.currentLang === 'en' ? en : ar;
  }

  toggleMenu(): void {
    this.isMenuOpen = !this.isMenuOpen;
    const navMenu = document.querySelector('.nav-menu');
    const hamburger = document.querySelector('.hamburger');
    
    if (navMenu) {
      navMenu.classList.toggle('active');
    }
    if (hamburger) {
      hamburger.classList.toggle('active');
    }
  }

  scrollToSection(sectionId: string): void {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      if (this.isMenuOpen) {
        this.toggleMenu();
      }
    }
  }

  private attachMobileMenuHandler(): void {
    const hamburger = document.querySelector('.hamburger');
    const navMenu = document.querySelector('.nav-menu');
    
    if (hamburger && navMenu) {
      hamburger.addEventListener('click', () => {
        this.toggleMenu();
      });
    }
  }

  private updateLanguageDirection(): void {
    const isArabic = this.currentLang === 'ar';
    document.documentElement.setAttribute('dir', isArabic ? 'rtl' : 'ltr');
    document.documentElement.setAttribute('lang', isArabic ? 'ar' : 'en');
    document.body.classList.toggle('lang-ar', isArabic);
  }
